select entrytime,coalesce(dat.sum,0) sum from (select period_add(extract(year_month from now()), -(@num:= @num + 1 )) as time from terminal ,(select @num:= -1) t where @num < ${months}-1 order by time ) timeunit left join (select extract(year_month from entryime) timekey2,count(*) sum FROM terminal
where 1=1
and entrytime >= #{beginTime}
and entrytime <= #{endTime}
AND name like CONCAT('%',#{user},'%')
AND address like CONCAT('%',#{accounts},'%')
AND productModel like CONCAT('%',#{productModel},'%')
AND ip = #{ip}
and extract(year_month from entrytime) = #{period_month}
AND mac = #{mac}
AND city like CONCAT('%',#{city},'%')
AND status like CONCAT('%',#{status},'%')
and entrytime >= DATE_SUB(NOW(),INTERVAL ${timeRange}) and entrytime <= NOW()
group by timekey2 ) dat on timeunit.timekey = dat.timekey2 order by entrytime

